import "./../UI/Todo.css";
import icon_check from "./../icon-check.svg";
import icon_cross from "./../icon-cross.svg";
import React, { useState } from "react";
import { useDrag } from "react-dnd";
import SortableList from "react-sortable-dnd-list/commonjs/SortableList";


const item = []
let i = 0
while (i < 10) {
  item.push({
    title: `List Item ${i + 1}`,
    description: 'Aeean aliquam molestie urna, vel aliquam.'
  })
  i++
}

function ItemComponent({
  dragging,
  dragged,
  children: { title, description },
  ...rest
}, props) {
  const FindIndex = () => {
    props.checkState(props.keys);
    props.CheckLen();
  };
  const onDelete = () => {
    props.Delete(props.keys)
  }
  return (
    <React.Fragment>
    <li {...rest} className={`list__item ${dragged ? 'is-dragging' : ''}`}>
      <div className="list-check list__item-content">
        <button className={`Checked Checked-${props.state} list__item-title`} onClick={FindIndex}>
          <img src={icon_check} alt="" />
          <div className={`Checked-Border Checked-Border-${props.state}`}></div>
        </button>
        <p className={`${props.state}`}>{props.value}</p>
      </div>
      <button onClick={onDelete} className="Deleted">
        <img src={icon_cross} alt="" />
      </button>
    </li>
    </React.Fragment>
  )
}

