import { useState } from "react";
import "./Todo.css";
import icon_moon from "./../icon-moon.svg";
import TodoList from "../TodoList/TodoList";
const TodoUi = () => {
  const [Data, SetData] = useState([]);
  const [Value, setValue] = useState("");
  const [State, setState] = useState("");
  const onSave = (e) => {
    setValue(e.target.value);
    if (e.target.value === "Ts") {
      setState('Complete')
    } else {
      setState("Active");
    }
  };
  const onLeft = () => {
    SetData([{ value: Value, state: State }, ...Data]);

  };
  return (
    <div className="continer">
      <div className="continerUser">
        <div className="Themes">
          <h1>TODO</h1>
          <img src={icon_moon} alt="" />
        </div>
        <div className="UserInput">
          <input type="text" onChange={onSave} value={Value} onBlur={onLeft} />
        </div>
        {Data.map(el => {
          return <TodoList value={el.value} />
        })}
      </div>
    </div>
  );
};

export default TodoUi;
