import { useState } from "react";
import "./Todo.css";
import icon_moon from "./../icon-moon.svg";
import TodoList from "../TodoList/TodoList";
const TodoUi = () => {
  const [Data, SetData] = useState([]);
  const [Value, setValue] = useState("");
  const [State, setState] = useState("");
  const onSave = (e) => {
    setValue(e.target.value);
    if (e.target.value === "Ts") {
      setState("Complete");
    } else {
      setState("Active");
    }
  };
  const onLeft = (e) => {
    if (e.target.value.length === 0) {
      console.log("Error");
    } else {
      SetData([
        { value: Value, state: State, Key: Math.random().toString() },
        ...Data,
      ]);
    }
  };
  return (
    <div className="continer">
      <div className="continerUser">
        <div className="Themes">
          <h1>TODO</h1>
          <img src={icon_moon} alt="" />
        </div>
        <div className="UserInput">
          <input type="text" onChange={onSave} value={Value} onBlur={onLeft} />
        </div>
        {Data.length === 0 ? (
          <TodoList value={"There are Nothing Here"} DataLen={Data.length} />
        ) : (
          Data.map((el) => {
            return <TodoList value={el.value} DataLen={Data.length} keys={el.Key}/>;
          })
        )}
      </div>
    </div>
  );
};

export default TodoUi;
