import "./../UI/Todo.css";
import icon_check from "./../icon-check.svg";
import icon_cross from "./../icon-cross.svg";
import React, { useState } from "react";
import { ItemType } from "../../utils/items";
import { useDrag } from "react-dnd";
import SortableList from "react-sortable-dnd-list/commonjs/SortableList";
const item = []
let i = 0 
while ( i < 10) {
  item.push({
    title: `List Item ${i + 1}`,
    desc: 'Test'
  })
  i++
}

function ItemComponent() {
  const [{ opacity }, dragRef] = useDrag(
    () => ({
      type: ItemType.CARD,
      collect: (monitor) => ({
        opacity: monitor.isDragging() ? 0.5 : 1
      })
    }),
    []
  )
  // const FindIndex = () => {
  //   props.checkState(props.keys);
  //   props.CheckLen();
  // };
  // const onDelete = () => {
  //   props.Delete(props.keys)
  // }
  return (
    <li ref={dragRef} style={{opacity}}>
      <div className="list-check">
        <button>
          <img src={icon_check} alt="" />
          <div></div>
        </button>
        <p>{item.title}</p>
      </div>
      <button className="Deleted">
        <img src={icon_cross} alt="" />
      </button>
    </li>
  );
}
const TodoResults = (props) => {
  const [items, setItems] = useState(item)
  return <SortableList itemComponent={ItemComponent} value={items} onChange={setItems} />
};
export default TodoResults;
