import "./../UI/Todo.css";
import icon_check from "./../icon-check.svg";
import icon_cross from "./../icon-cross.svg";
import React, { useState } from "react";
import { useDrag } from "react-dnd";
import SortableList from "react-sortable-dnd-list/commonjs/SortableList";


const items = []
let i = 0
while (i < 10) {
  items.push({
    title: `List Item ${i + 1}`,
    description: 'Aenean aliquam molestie urna, vel aliquam.'
  })
  i++
}

function ItemComponent({
  dragging,
  dragged,
  children: { title, description },
  ...rest
}) {
  return (
    <div {...rest} className={`list__item ${dragged ? 'is-dragging' : ''}`}>
      <div className="list__item-content">
        <div className="list__item-title">
          {title}
        </div>
        <div className="list__item-description">
          {description}
        </div>
      </div>
    </div>
  )
}

