import update from "immutability-helper";
import { useCallback, useState } from "react";
import { DndProvider, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { ItemType } from "../../utils/items";
import "./../UI/Todo.css";
import TodoResults from "./TodoResults";
const TodoList = (props) => {
  const [cards, setCards] = useState(props.Data);
  const findCard = useCallback(
    (id) => {
      const card = cards.filter((c) => `${c.id}` === id)[0];
      return {
        card,
        index: cards.indexOf(card),
      };
    },
    [cards]
  );


  const btns = document.getElementsByClassName("Checked-btns");
  const [checkState, setCheckState] = useState("All");
  const [Datalen, setDataLen] = useState(0);
  const CheckLen = () => {
    const GetLen = props.Data.filter((el) => {
      return el.state === "Complete";
    });
    setDataLen(GetLen.length);
  };

  const onAll = (e) => {
    for (let i = 0; i < btns.length; i++) {
      btns[i].style.color = "hsl(236, 9%, 61%)";
    }
    btns[0].style.color = "hsl(220, 98%, 61%)";
    setCheckState("All");
  };

  const onActive = () => {
    for (let i = 0; i < btns.length; i++) {
      btns[i].style.color = "hsl(236, 9%, 61%)";
    }
    btns[1].style.color = "hsl(220, 98%, 61%)";
    setCheckState("Active");
  };
  const onComplete = () => {
    for (let i = 0; i < btns.length; i++) {
      btns[i].style.color = "hsl(236, 9%, 61%)";
    }
    btns[2].style.color = "hsl(220, 98%, 61%)";
    setCheckState("Complete");
  };
  const onClear = () => {
    setDataLen(0);
    props.onClearComplete();
  };
  return (
    <ul className="UserResults">
      <DndProvider backend={HTML5Backend}>
        <div>
          {props.Data.length === 0 ? (
            <TodoResults value={"There are Nothing Here"} id={"arsa"} />
          ) : checkState === "All" ? (
            props.Data.map((el) => {
              return (
                <TodoResults
                  value={el.value}
                  state={el.state}
                  id={el.id}
                  key={el.id}
                  checkState={props.onCheckState}
                  CheckLen={CheckLen}
                  Delete={props.onDelete}
                  findCard={findCard}
                />
              );
            })
          ) : checkState === "Active" ? (
            props.Data.map((el) => {
              return el.state === "Active" ? (
                <TodoResults
                  value={el.value}
                  state={el.state}
                  id={el.id}
                  key={el.id}
                  checkState={props.onCheckState}
                  CheckLen={CheckLen}
                  Delete={props.onDelete}
                  findCard={findCard}
                />
              ) : (
                console.log("Error")
              );
            })
          ) : checkState === "Complete" ? (
            props.Data.map((el) => {
              return el.state === "Complete" ? (
                <TodoResults
                  value={el.value}
                  state={el.state}
                  id={el.id}
                  key={el.id}
                  checkState={props.onCheckState}
                  CheckLen={CheckLen}
                  Delete={props.onDelete}
                  findCard={findCard}
                />
              ) : (
                console.log("Error")
              );
            })
          ) : (
            console.log("Error")
          )}
        </div>
      </DndProvider>
      <div className="active-states">
        <p>{props.Data.length - Datalen} items left</p>
        <div className="check-btns">
          <button className="Checked-btns" onClick={onAll}>
            All
          </button>
          <button className="Checked-btns" onClick={onActive}>
            Active
          </button>
          <button className="Checked-btns" onClick={onComplete}>
            Completed
          </button>
        </div>
        <button onClick={onClear}>Clear Completed</button>
      </div>
    </ul>
  );
};
export default TodoList;
