import { useState } from "react";
import "./../UI/Todo.css";
import TodoResults from "./TodoResults";
const TodoList = (props) => {
  let content = "";
  const [Datalen, setDataLen] = useState(0);
  const CheckLen = () => {
    const GetLen = props.Data.filter((el) => {
      return el.state === "Complete";
    });
    setDataLen(GetLen.length);
  };

  const onAll = () => {
    content = props.Data.map((el) => {
      return (
        <TodoResults
          value={el.value}
          state={el.state}
          keys={el.keys}
          checkState={props.onCheckState}
          CheckLen={CheckLen}
        />
      );
    });
  };
  const onActive = () => {
    content = props.Data.filter((el) => {
      return (
        el.state === "Active" && (
          <TodoResults
            value={el.value}
            state={el.state}
            keys={el.keys}
            checkState={props.onCheckState}
            CheckLen={CheckLen}
          />
        )
      );
    });
  };
  const onCompleted = () => {
    content = props.Data.filter((el) => {
      return (
        el.state === "Complete" && (
          <TodoResults
            value={el.value}
            state={el.state}
            keys={el.keys}
            checkState={props.onCheckState}
            CheckLen={CheckLen}
          />
        )
      );
    });
  };

  return (
    <ul className="UserResults">
      {props.Data.length === 0 ? (
        <TodoResults value={"There are Nothing Here"} keys={"arsa"} />
      ) : (
        props.Data.map((el) => {
          return (
            <TodoResults
              value={el.value}
              state={el.state}
              keys={el.keys}
              checkState={props.onCheckState}
              CheckLen={CheckLen}
            />
          );
        })
      )}
      <div className="active-states">
        <p>{props.Data.length - Datalen} items left</p>
        <div className="check-btns">
          <button onClick={onAll}>All</button>
          <button onClick={onActive}>Active</button>
          <button onClick={onCompleted}>Completed</button>
        </div>
        <button>Clear Completed</button>
      </div>
    </ul>
  );
};
export default TodoList;
