import { useState } from "react";
import "./Todo.css";
import icon_moon from "./../icon-moon.svg";
import TodoList from "../TodoList/TodoList";
const TodoUi = () => {
  const [Data, SetData] = useState([]);
  const [Value, setValue] = useState("");
  const [State, setState] = useState("");
  const onSave = (e) => {
    setValue(e.target.value);
    if (e.target.value === "Test") {
      setState('Complete')
    } else {
      setState("Active");
    }
  };
  const onLeft = () => {
    console.log(Data.length)
    if (Data.length === 1) {
      SetData({ value: Value, state: State });
    }else {
      SetData({ value: Value, state: State }, ...Data);
    }
  };
  console.log(Data);
  return (
    <div className="continer">
      <div className="continerUser">
        <div className="Themes">
          <h1>TODO</h1>
          <img src={icon_moon} alt="" />
        </div>
        <div className="UserInput">
          <input type="text" onChange={onSave} value={Value} onBlur={onLeft} />
        </div>
        <TodoList />
      </div>
    </div>
  );
};

export default TodoUi;
