import update from "immutability-helper";
import { useCallback, useState } from "react";
import { useDrop } from "react-dnd";
import { ItemType } from "../../utils/items";
import "./../UI/Todo.css";
import TodoResults from "./TodoResults";
const ITEMS = [
  {
    id: 1,
    text: 'Write a cool JS library',
  },
  {
    id: 2,
    text: 'Make it generic enough',
  },
  {
    id: 3,
    text: 'Write README',
  },
  {
    id: 4,
    text: 'Create some examples',
  },
  {
    id: 5,
    text: 'Spam in Twitter and IRC to promote it',
  },
  {
    id: 6,
    text: '???',
  },
  {
    id: 7,
    text: 'PROFIT',
  },
]
const TodoList = (props) => {
  const [cards, setCards] = useState(ITEMS)
  const findCard = useCallback(
    (id) => {
      const card = cards.filter((c) => `${c.id}` === id)[0]
      return {
        card,
        index: cards.indexOf(card),
      }
    },
    [cards],
  )
  const moveCard = useCallback(
    (id, atIndex) => {
      const { card, index } = findCard(id)
      setCards(
        update(cards, {
          $splice: [
            [index, 1],
            [atIndex, 0, card],
          ],
        }),
      )
    },
    [findCard, cards, setCards],
  )
  const [, drop] = useDrop(() => ({ accept: ItemType.CARD }))

  const btns = document.getElementsByClassName("Checked-btns");
  const [checkState, setCheckState] = useState("All");
  const [Datalen, setDataLen] = useState(0);
  const CheckLen = () => {
    const GetLen = props.Data.filter((el) => {
      return el.state === "Complete";
    });
    setDataLen(GetLen.length);
  };

  const onAll = (e) => {
    for (let i = 0; i < btns.length; i++) {
      btns[i].style.color = "hsl(236, 9%, 61%)";
    }
    btns[0].style.color = "hsl(220, 98%, 61%)";
    setCheckState("All");
  };

  const onActive = () => {
    for (let i = 0; i < btns.length; i++) {
      btns[i].style.color = "hsl(236, 9%, 61%)";
    }
    btns[1].style.color = "hsl(220, 98%, 61%)";
    setCheckState("Active");
  };
  const onComplete = () => {
    for (let i = 0; i < btns.length; i++) {
      btns[i].style.color = "hsl(236, 9%, 61%)";
    }
    btns[2].style.color = "hsl(220, 98%, 61%)";
    setCheckState("Complete");
  };
  const onClear = () => {
    setDataLen(0);
    props.onClearComplete();
  };
  console.log(props.Data)
  return (
    <ul className="UserResults">
        <div ref={drop}>
          {props.Data.length === 0 ? (
            <TodoResults
              value={"There are Nothing Here"}
              moveCard={moveCard}
              findCard={findCard}
              id={"arsa"}
            />
          ) : checkState === "All" ? (
            cards.map((card) => {
              return (
                <TodoResults
                  value={card.text}
                  state={card.state}
                  id={`${card.id}`}
                  key={card.id}
                  checkState={props.onCheckState}
                  CheckLen={CheckLen}
                  Delete={props.onDelete}
                  moveCard={moveCard}
                  findCard={findCard}
                />
              );
            })
          ) : checkState === "Active" ? (
            props.Data.map((el) => {
              return el.state === "Active" ? (
                <TodoResults
                  value={el.value}
                  state={el.state}
                  id={el.id}
                  key={el.id}
                  checkState={props.onCheckState}
                  CheckLen={CheckLen}
                  Delete={props.onDelete}
                  moveCard={moveCard}
                  findCard={findCard}
                />
              ) : (
                console.log("Error")
              );
            })
          ) : checkState === "Complete" ? (
            props.Data.map((el) => {
              return el.state === "Complete" ? (
                <TodoResults
                  value={el.value}
                  state={el.state}
                  id={el.id}
                  key={el.id}
                  checkState={props.onCheckState}
                  CheckLen={CheckLen}
                  Delete={props.onDelete}
                  moveCard={moveCard}
                  findCard={findCard}
                />
              ) : (
                console.log("Error")
              );
            })
          ) : (
            console.log("Error")
          )}
        </div>
      <div className="active-states">
        <p>{props.Data.length - Datalen} items left</p>
        <div className="check-btns">
          <button className="Checked-btns" onClick={onAll}>
            All
          </button>
          <button className="Checked-btns" onClick={onActive}>
            Active
          </button>
          <button className="Checked-btns" onClick={onComplete}>
            Completed
          </button>
        </div>
        <button onClick={onClear}>Clear Completed</button>
      </div>
    </ul>
  );
};
export default TodoList;
