import { useEffect, useState } from "react";
import "./../UI/Todo.css";
import TodoResults from "./TodoResults";
const TodoList = (props) => {
  const [content, setContent] = useState("");
  const [checkState, setCheckState] = useState('All')
  const [Datalen, setDataLen] = useState(0);
  const CheckLen = () => {
    const GetLen = props.Data.filter((el) => {
      return el.state === "Complete";
    });
    setDataLen(GetLen.length);
  };

  const onAll = () => {
    setContent(
      props.Data.map((el) => {
        return (
          <TodoResults
            value={el.value}
            state={el.state}
            keys={el.keys}
            checkState={props.onCheckState}
            CheckLen={CheckLen}
          />
        );
      })
    );
    console.log(content);
  };


  const onActive = () => {
    setContent(
      props.Data.map((el) => {
        return el.state === "Active" ? (
          <TodoResults
            value={el.value}
            state={el.state}
            keys={el.keys}
            checkState={props.onCheckState}
            CheckLen={CheckLen}
          />
        ) : (
          console.log("Error")
        );
      })
    );
  };
  const onCompleted = () => {
    setContent(
      props.Data.map((el) => {
        return el.state === "Complete" ? (
          <TodoResults
            value={el.value}
            state={el.state}
            keys={el.keys}
            checkState={props.onCheckState}
            CheckLen={CheckLen}
          />
        ) : (
          console.log("Error")
        );
      })
    );
  };
useEffect(() => {
  if(checkState === 'All' ) {
    onAll()
  }
}, [props.value])
  return (
    <ul className="UserResults">
      {props.Data.length === 0 ? (
        <TodoResults value={"There are Nothing Here"} keys={"arsa"} />
      ) : (
        content
      )}
      <div className="active-states">
        <p>{props.Data.length - Datalen} items left</p>
        <div className="check-btns">
          <button onClick={onAll}>All</button>
          <button onClick={onActive}>Active</button>
          <button onClick={onCompleted}>Completed</button>
        </div>
        <button>Clear Completed</button>
      </div>
    </ul>
  );
};
export default TodoList;
