import { useState } from "react";
import "./Todo.css";
import icon_moon from "./../icon-moon.svg";
import TodoList from "../TodoList/TodoList";
const TodoUi = () => {
  const [Data, SetData] = useState([]);
  const [Value, setValue] = useState("");
  const onSave = (e) => {
    setValue(e.target.value);
  };
  const onLeft = (e) => {
    if (e.target.value.length === 0) {
      console.log("Error");
    } else {
      SetData([
        { value: Value, state: "Active", keys: Math.random().toString() },
        ...Data,
      ]);
    }
    setValue("");
  };
  const onDelete = (AllState) => {
    for (const i in Data) {
      if (Data[i].keys === AllState) {
        Data[i].state = "Delete";
      }
    }
    const test = Data.filter((el) => {
      return el.state !== 'Delete'
    })
    console.log(test)
    SetData(test);
  };
  const onCheckState = (AllState) => {
    for (const i in Data) {
      if (Data[i].keys === AllState) {
        Data[i].state = "Complete";
      }
    }
    SetData(Data);
  };
  const onClearComplete = () => {
    SetData(
      Data.filter((el) => {
        return el.state !== "Complete";
      })
    );
  };
  return (
    <div className="continer">
      <div className="continerUser">
        <div className="Themes">
          <h1>TODO</h1>
          <img src={icon_moon} alt="" />
        </div>
        <div className="UserInput">
          <input type="text" onChange={onSave} value={Value} onBlur={onLeft} />
        </div>
        <TodoList
          Data={Data}
          onCheckState={onCheckState}
          onLeft={onLeft}
          onClearComplete={onClearComplete}
          value={Value}
          onDelete={onDelete}
        />
      </div>
    </div>
  );
};

export default TodoUi;
