import { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

import "./Todo.css";
import icon_moon from "./../icon-moon.svg";
import icon_sun from "./../icon-sun.svg";
import TodoList from "../TodoList/TodoList";
const TodoUi = (props) => {
  const [Light, setLight] = useState(true);
  const Dark_Light = () => {
    setLight((prev) => !prev);
    props.onLight();
    document.querySelector(".UserInput input").style.backgroundColor = !Light
      ? "white"
      : "hsl(235, 24%, 19%)";
    document.querySelector(".UserResults").style.backgroundColor = !Light
      ? "white"
      : "hsl(235, 24%, 19%)";
    document.querySelector(".list-check img").style.display = !Light
      ? "block"
      : "block";
    document.querySelector(
      ".list-check .Checked-Border"
    ).style.backgroundColor = "transparent";
    document.querySelector(".UserResults li").style.borderColor = !Light
      ? "white"
      : "hsl(235deg 19% 35% / 37%)";
    document.querySelector(".Active").style.color = !Light
      ? "hsl(235, 19%, 35%)"
      : "hsl(234, 39%, 85%)";
  };

  const [Data, SetData] = useState([]);
  const [Value, setValue] = useState("");
  const onSave = (e) => {
    setValue(e.target.value);
  };
  const onLeft = (e) => {
    if (e.target.value.length === 0) {
      console.log("Error");
    } else {
      SetData([
        { value: Value, state: "Active", keys: Math.random().toString() },
        ...Data,
      ]);
    }
    setValue("");
  };
  const onDelete = (AllState) => {
    for (const i in Data) {
      if (Data[i].keys === AllState) {
        Data[i].state = "Delete";
      }
    }
    const test = Data.filter((el) => {
      return el.state !== "Delete";
    });
    console.log(test);
    SetData(test);
  };
  const onCheckState = (AllState) => {
    for (const i in Data) {
      if (Data[i].keys === AllState) {
        Data[i].state = "Complete";
      }
    }
    SetData(Data);
  };
  const onClearComplete = () => {
    SetData(
      Data.filter((el) => {
        return el.state !== "Complete";
      })
    );
  };
  return (
    <div className="continer">
      <div className="continerUser">
        <div className="Themes">
          <h1>TODO</h1>
          <img onClick={Dark_Light} src={Light ? icon_moon : icon_sun} alt="" />
        </div>
        <div className="UserInput">
          <input
            type="text"
            onChange={onSave}
            value={Value}
            onBlur={onLeft}
            placeholder="Create a new todo..."
          />
        </div>
        <DndProvider backend={HTML5Backend}>
          <TodoList
            Data={Data}
            onCheckState={onCheckState}
            onLeft={onLeft}
            onClearComplete={onClearComplete}
            value={Value}
            onDelete={onDelete}
          />
        </DndProvider>
      </div>
    </div>
  );
};

export default TodoUi;
